mapper.select:
SELECT * FROM Board
WHERE itemCategoryId = #{itemCategoryId}
AND boardCategoryId = #{boardCategoryId}
ORDER BY isMegaphone DESC, regDate DESC;

SELECT * FROM Board
WHERE itemName LIKE CONCAT('%', #{searchTerm}, '%')
ORDER BY isMegaphone DESC, regDate DESC;

mapper.update:
UPDATE Board
SET hits = hits + 1
WHERE boardId = #{boardId}

UPDATE BOARD
SET interestCnt = interestCnt + 1
WHERE boardId = #{boardId}

UPDATE BOARD
SET interestCnt = interestCnt - 1
WHERE boardId = #{boardId}

mapper.select:
SELECT COUNT(*)
FROM FAVORITE
WHERE boardId = #{boardId}

mapper.update:
UPDATE BOARD
SET isAuction = #{state}
WHERE boardId = #{boardId}

mapper.select:
SELECT itemImage1
FROM BOARD
WHERE boardId = #{boardId}

SELECT itemImage2
FROM BOARD
WHERE boardId = #{boardId}

SELECT itemImage3
FROM BOARD
WHERE boardId = #{boardId}

SELECT latitude, longitude
FROM board
WHERE boardId = #{boardId}

mapper.update:
UPDATE board
SET isMegaphone = false
WHERE isMegaphone = true AND TIMESTAMPDIFF(WEEK, regDate, NOW()) >= 1;
