mapper.select:
select boardId, title, writer, itemImage1, isLend from board

select title,writer,itemImage1,boardId from board


    boardId = #{boardId}

select `from`, `to`, chatId, content, sendTime from message
chatId = #{chatId}
ORDER BY sendTime asc

mapper.update:
UPDATE message
SET
`from` = #{from},
`to` = #{to},
sendTime = #{sendTime},
content = #{content},
latitude = #{latitude},
longitude = #{longitude}
WHERE chatId = #{chatId}
and messageId = #{messageId}

mapper.select:
select latitude, longitude, messageId from message
chatId = #{chatId}
and latitude is not null
