mapper.select:
select count(*) as writerCnt,b.writer from report r inner join board b on r.boardId = b.boardId inner join user u on b.writer= u.userId
u.ban=false
group by b.writer order by b.writer limit #{limit} offset #{offset}

select r.userId,r.title,r.content,r.regDate from
report r inner join board b
on r.boardId=b.boardId

    b.writer=#{writer}

order by r.reportId limit #{limit} offset #{offset}
